select sounding2k18.date, cape, cape_virt, conv_inhib, cins, pressure , temp , mixr, relhumidity, snkt as windspeed, drct as wind_direction , pm10, aot_440 , ae from sounding2k18 , (select date, avg(pm10) as pm10 from pm10_catano group by date) as pm10, (select date, avg(aot_440) as aot_440 , avg(angstrom440_870) as ae from aotsanjuan group by date) as aot where sounding2k18.date between 2005-01-01 and '2016-12-31' and time = '12:00:00' and sounding2k18.date = pm10.date and sounding2k18.date = aot.date and pressure in (select * from StandardPressures) order by date;
