select sounding1.date, cape, cape_virt, conv_inhib, cins, pressure , temp , mixr, relhumidity, snkt as windspeed, drct as wind_direction , aot_440 , ae from sounding1 , (select date, avg(aot_440) as aot_440 , avg(angstrom440_870) as ae from aotv2k18 group by date) as aot where sounding1.station_number = 78897 and sounding1.date between '2005-01-01' and '2016-12-31' and time = '12:00:00' and sounding1.date = aot.date and pressure in (select * from StandardPressures) order by date;
